import { useEffect, useState } from 'react';
import { NavLink, useHistory, useParams } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { reviewCreate, reviewDelete, reviewEdit } from '../../store/reviews';

function Reviews() {
	const dispatch = useDispatch();
	const history = useHistory();
	const reviews = useSelector((state) => state.reviews);
	const user = useSelector((state) => state.session.user);
	const illusion = useSelector((state) => state.illusions);

	const [title, setTitle] = useState('');
	const [description, setDescription] = useState('');

	const updateTitle = (e) => setTitle(e.target.value);
	const updateDescription = (e) => setDescription(e.target.value);

	const handleSubmit = async (e) => {
		e.preventDefault();

		const payload = {
			reviewId: reviews.id,
			title,
			description,
			userId: user.id,
			illusionId: illusion.id,
		};

		let updatedReviewDelete = await dispatch(
			reviewDelete(payload.reviewId)
		);
		if (updatedReviewDelete) {
			history.push(`/categories/${updatedReviewDelete.id}`);
		}

		let updatedReviewEdit = await dispatch(reviewEdit(payload));
		if (updatedReviewEdit) {
			history.push(`/illusion/${illusion.id}`);
		}
	};

	return (
		<div>
			<h1>Review Test</h1>
		</div>
	);
}

export default Reviews;
