import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useHistory, NavLink } from 'react-router-dom';
import { loadCategory } from '../../store/categories';
import { illusionCreate } from '../../store/illusion';
import { homeCategory } from '../../store/home-category';
import ProfileButton from '../Navigation/ProfileButton';
import logotxt from './images/logotxt-create.png';
import logo from './images/logo.jpeg';

const CreateIllusionForm = ({ isLoaded }) => {
	const illusion = useSelector((state) => state.illusionCreate);
	console.log(illusion);
	const categories = useSelector((state) => {
		return state.homeCategory.map((el) => {
			return el;
		});
	});
	const dispatch = useDispatch();
	const history = useHistory();

	const [title, setTitle] = useState('');
	const [image, setImage] = useState('');
	const [description, setDescription] = useState('');
	const [steps, setSteps] = useState('');

	const updateTitle = (e) => setTitle(e.target.value);
	const updateImage = (e) => setImage(e.target.value);
	const updateDescription = (e) => setDescription(e.target.value);
	const updateSteps = (e) => setSteps(e.target.value);

	useEffect(() => {
		dispatch(illusionCreate());
		dispatch(homeCategory());
	}, [dispatch]);

	const handleSubmit = async (e) => {
		e.preventDefault();

		const payload = {
			title,
			image,
			description,
			steps,
		};

		let createdIllusion = await dispatch(illusion(payload));
		console.log(createdIllusion);
		if (createdIllusion) {
			history.push(`/create/${createdIllusion.id}`);
		}
	};

	const sessionUser = useSelector((state) => state.session.user);

	let sessionLinks;
	if (sessionUser) {
		sessionLinks = <ProfileButton user={sessionUser} />;
	}

	return (
		<>
			<div className='nav-bar__container'>
				<div className='nav-bar-logo__container'>
					<img
						alt='navbar-logotxt'
						className='nav-bar__logotxt'
						src={logotxt}></img>
				</div>
				<ul className='nav-bar-ul'>
					{isLoaded && sessionLinks}
					<li className='nav-bar__link'>
						<NavLink exact to='/' className='nav-bar'>
							Discover
						</NavLink>
					</li>
					<li className='nav-bar__link'>
						<NavLink
							exact
							to='/illusions/create'
							className='nav-bar'>
							Create
						</NavLink>
					</li>
				</ul>
			</div>
			<div className='home-logo__container'>
				<div className='title-logo__container'>
					<img
						alt='title-logo'
						className='title-logo'
						src={logo}></img>
					<img
						alt='title-logo-txt'
						className='title-logotxt'
						src={logotxt}></img>
				</div>
				<div className='title'></div>
				<div className='title-container'>
					<h1 className='title'>Discover, Create, Show off</h1>
					<h2 className='subtitle'>Time to Show Off ...!</h2>
					<h2 className='subtitle'>Show us what you can do!</h2>
				</div>
			</div>
			<div className='form-container'>
				<form
					onSubmit={handleSubmit}
					className='initial-form signup-form'>
					<label className='form-label'>
						Select a Category
						<select className='form-input'>
							{categories.map((el) => {
								<option>{el.title}</option>;
							})}
						</select>
					</label>
					<label className='form-label'>
						Illusion Title
						<input
							className='form-input'
							type='text'
							value={title}
							onChange={updateTitle}
							required
							placeholder='Ex. Three Card Monte'
						/>
					</label>
					<label className='form-label'>
						Illusion URL Image
						<input
							className='form-input'
							type='text'
							value={image}
							onChange={updateImage}
							required
							placeholder='Copy the URL here'
						/>
					</label>
					<label className='form-label input-description'>
						Illusion Description
						<input
							className='form-input'
							type='textarea'
							value={description}
							onChange={updateDescription}
							required
							placeholder='Ex. What is the main effect of your illusion'
						/>
					</label>
					<label className='form-label input-steps'>
						Illusion Steps
						<input
							className='form-input'
							type='textarea'
							value={steps}
							onChange={updateSteps}
							placeholder='Ex. How is this Illuision done'
						/>
					</label>
					<div className='form-btn__container'>
						<button type='submit' className='form-btn'>
							Create
						</button>
					</div>
				</form>
			</div>
		</>
	);
};

export default CreateIllusionForm;
